CREATE TABLE tb_authentication_info (id int NOT NULL AUTO_INCREMENT, creation_time datetime(3) NOT NULL, user_id int NOT NULL, type varchar(32) NOT NULL, ip varchar(32) NOT NULL, device json DEFAULT NULL, province varchar(64) DEFAULT NULL, city varchar(64) DEFAULT NULL, area varchar(64) DEFAULT NULL, sync_status tinyint DEFAULT '0', retry_count tinyint DEFAULT '0', remark varchar(512) DEFAULT NULL COMMENT '备注',PRIMARY KEY (id)) ENGINE=InnoDB;